    public void cuadroDialogo(Context context,
                              DialogInterface.OnClickListener confirmar,
                              String titulo,
                              String mensaje) {
        AlertDialog.Builder dialogo1 = new AlertDialog.Builder(context);//creamos el mensaje de aceptacion o cancelacion
        dialogo1.setTitle(titulo);
        dialogo1.setMessage(mensaje);
        dialogo1.setCancelable(false);
        dialogo1.setPositiveButton("Confirmar", confirmar);
        dialogo1.setNegativeButton("Cancelar", new DialogInterface.OnClickListener() {
            public void onClick(DialogInterface dialogo1, int id) {
                dialogo1.dismiss();
            }
        });
        dialogo1.show();
    }

    public void actualizarMensajes(DatabaseReference databaseReference) {
        databaseReference.child("Mensaje").addValueEventListener(new ValueEventListener() {
            @Override
            public void onDataChange(@NonNull DataSnapshot snapshot) {
                if (snapshot.exists()) {
                    for (DataSnapshot ds : snapshot.getChildren()) {
                        Mensaje mensaje = ds.getValue(Mensaje.class);
                        if (mensaje != null && mensaje.getEsEliminado() == null) {
                            Map<String, Object> map = new HashMap<>();
                            map.put("esEliminado", false);

                            databaseReference.child("Mensaje").child(mensaje.getIdMensaje()).updateChildren(map).addOnSuccessListener(new OnSuccessListener<Void>() {
                                @Override
                                public void onSuccess(Void aVoid) {
                                    Log.w("Actualizado", mensaje.getIdMensaje());
                                }
                            }).addOnFailureListener(new OnFailureListener() {
                                @Override
                                public void onFailure(@NonNull Exception e) {
                                    Log.w("ERROR", mensaje.getIdMensaje());
                                }
                            });
                        }

                    }

                }
            }

            @Override
            public void onCancelled(@NonNull DatabaseError error) {

            }
        });


    }

