    public Bitmap cargarIconoTienda(){
        byte[] decodedString = Base64.decode(iconoTienda, Base64.DEFAULT);
        Bitmap decodedByte = BitmapFactory.decodeByteArray(decodedString, 0, decodedString.length);
        decodedByte = Bitmap.createScaledBitmap(decodedByte,150,150,false);
        return decodedByte;
    }

    public Bitmap cargarIconocliente(){
        byte[] decodedString = Base64.decode(iconoCliente, Base64.DEFAULT);
        Bitmap decodedByte = BitmapFactory.decodeByteArray(decodedString, 0, decodedString.length);
        decodedByte = Bitmap.createScaledBitmap(decodedByte,150,150,false);
        return decodedByte;
    }

    public Dialog cuadroError(Context context,String msgError){
        final Dialog dialog = new Dialog(context);
        dialog.requestWindowFeature(Window.FEATURE_NO_TITLE);//el dialogo se presenta sin el titulo
        dialog.setCancelable(false); //impedimos el cancelamiento del dialogo
        //dialog.getWindow().setBackgroundDrawable(new ColorDrawable(Color.));//le damos un color de fondo transparente
        dialog.setContentView(R.layout.cuadro_error); //le asisganos el layout
        TextView textView = dialog.findViewById(R.id.txtCuadroError);
        textView.setText(msgError);
        return dialog;
    }

    public void buscarClientebloqueado(Context context, FirebaseAuth firebaseAuth, DatabaseReference databaseReference){
        Query query = databaseReference.child("Cliente").orderByChild("uidUsuario").equalTo(firebaseAuth.getCurrentUser().getUid());
        query.addValueEventListener(new ValueEventListener() {
            @Override
            public void onDataChange(@NonNull DataSnapshot snapshot) {
                if (snapshot.exists()){
                    Cliente cliente = null;
                    for (DataSnapshot ds:snapshot.getChildren()) {
                        cliente = ds.getValue(Cliente.class);
                    }
                    if(cliente !=null){
                        if (cliente.getBloqueado()){
                            Dialog dialog = cuadroError(context,"Usted ha sido bloqueado, pongase en contacto con el vendedor");
                        }
                    }
                }
            }

            @Override
            public void onCancelled(@NonNull DatabaseError error) {

            }
        });
    }

